package Chapter46;
public class arraySum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Variable
		int sum;
		int[] val = {0, 1, 2, 3}; 
	    //Processing 
	    sum = val[0] + val[1] + val[2] + val[3];
	    //Output
	    System.out.println( "Sum of all numbers = " + sum );
		 
	}

}
